import React, { Component } from 'react'

export default class Demo extends Component {

    state = {count:0}

    increment = ()=>{
        const {count} = this.count
        this.setState({count:count+1})
    }

    render() {
        return (
            <div>
                <h1>当前求和为:{this.state.count}</h1>
                <button onClick={this.increment}>点我+1</button>
            </div>
        )
    }
}
