function Demo(){

    const [count,setCount] =React.useState(0)
    const [name,setName] =React.useState('tom')


    function add(){
        //setCount(count+1) 第一种写法
        setCount(count=>count+1)
    }
    function changeName(){
        setName('jack')
    }
    return(
        <div>
            <h2>当前求和为:{count}</h2>
            <h2>我的名字是:yeqiang</h2>
            <button onClick={add}>点我+1</button>
            <button onClick={changeName}>点我改名</button>
        </div>
    )
}

export default Demo
