//引入Count的UI组件
import CountUI from '../../components/Count'

//引入connect用于链接UI组件和redux
import {connect} from 'react-redux'

//a函数的返回值作为状态传递给了UI组件,返回对象中的Key就作为传递给UI组件props的key,value作为props的value---状态
function a(){
    return {n:900}
}
//a函数的返回值作为状态传递给了UI组件,返回对象中的Key就作为传递给UI组件props的key,value作为props的value---操作状态的方法
