//引入Count的UI组件
import CountUI from '../../components/Count'

//引入connect用于链接UI组件和redux
import {connect} from 'react-redux'
import {INCREMENT,DECREMENT} from '../../components/reduxs/constant'



//a函数的返回值作为状态传递给了UI组件,返回对象中的Key就作为传递给UI组件props的key,value作为props的value---状态
function a(state){
    return {count:state}
}
//a函数的返回值作为状态传递给了UI组件,返回对象中的Key就作为传递给UI组件props的key,value作为props的value---操作状态的方法
function b(dispatch){
    return {jia:(number)=>{
        //通知redux执行加法
        dispatch(createIncrementAction(number))
    }}
}

//使用connect()()创建一个Count的容器组件
export default connect(a,b)(CountUI)
